import React, { useState } from "react";

function Form(){

  const [values, setValues] = useState({
        name : ' ',
        email : ' ',
        mobile : ' ',
        password : ' '
  })

  const [error, setError] = useState({
        name : ' ',
        email : ' ',
        mobile : ' ',
        password : ' '
  })


  const handleChange = (e) =>{
    const { name, value } = e.target;
    setValues({...values, [name]:value})
    let errorMessage = ' ';
    if(name === 'email' && !/\S+@\S+\.\S+/.test(value)){
      errorMessage = '* email not vailed';
    }
    if(name === 'name' && value.length < 5){
      errorMessage = '* name is requird'
    }
    if(name === 'mobile' && /^[0-9]{10}$/.test(value)){
      errorMessage = ' * mobile number not vailed ';
    }
    if(name === 'password' && value.length <6){
      errorMessage = '* password atlest six digt'
    }
    setError({...error, [name] : errorMessage})
  }

  function validateForm(){
    let newErrror = {};
    if(!values.length) newErrror.name = '* name is required';
    if(!values.length) newErrror.email = '* email is  required';
    if(!values.length) newErrror.mobile = '* mobile number is required';
    if(!values.length) newErrror.password = '* password is requird';
    setError(newErrror);
    return Object.keys(newErrror).length === 0;
  }

  function handleSubmit(e){
    e.preventDefault();
    if(validateForm()){
      console.log(values);
    }
  }

  return(
    <>
        <form onSubmit={handleSubmit} >
            <div>
                <label>Enter Name</label>
                <input 
                  type="text" 
                  autoComplete="off"
                  placeholder="Enter name" 
                  name="name" 
                  value={values.name}
                  onChange={handleChange} /> <br/>
               {error.name  && <span className="error">{error.name}</span>}
          
            </div>

            <div>
              <label>Enter Email</label>
              <input 
                type="text" 
                autoComplete="off"
                placeholder="email" 
                name="email" 
                value={values.email}
                onChange={handleChange} /> <br/>
              {error.email && <span className="error">{error.email}</span>}
            </div>

            <div>
              <label>Enter Mobile</label>
              <input 
                type="number" 
                autoComplete="off"
                placeholder="number" 
                value={values.mobile}
                name="mobile" 
                onChange={handleChange} /> <br/>
             {error.mobile && <span className="error">{error.mobile}</span>}
            </div>

            <div>
              <label>Enter Password</label>
              <input 
                type="password" 
                autoComplete="off"
                placeholder="password" 
                name="password" 
                value={values.password}
                onChange={handleChange} /> <br/>
             {error.password && <span className="error">{error.password}</span>}
            </div>

            <input type="submit" value="create" />
        </form>
    </>
  )
}
export default Form;

