import React, { useState, useEffect } from 'react';
import Slider from '@mui/material/Slider';
import Box from '@mui/material/Box';
import Chart from "react-apexcharts";
import { numberWithCommas } from '../utils/config';

function HomeLoan() {
    const [options, setOptions] = useState({
        chart: {
            type: 'donut',
        },
        labels: ['A', 'C'],
        legend: {
            show: false,
        },
        plotOptions: {
            pie: {
                donut: {
                    size: '70%',
                },
            },
        },
        dataLabels: {
            enabled: true,
        },
        stroke: {
            show: false,
        },
    });

    const [series, setSeries] = useState([41, 17]);
    const [cost, setCost] = useState(0);
    const [Intrest, setIntrest] = useState(10);
    const [fee, setFee] = useState(1);
    const [downPayment, setDownPayment] = useState(0);
    const [tenure, setTenure] = useState(0);
    const [emi, setEmi] = useState(0);
    const [values, setValues] = useState('');

    const handleChange = (e) => {
        setValues(e.target.value);
    };

    // Function to calculate EMI
    const calculateEMI = (loanAmount, interestRate, loanTenure) => {
        const rateOfInterest = interestRate / 100;
        const numOfMonths = loanTenure / 12;

        const EMI =
            (loanAmount * rateOfInterest * Math.pow(1 + rateOfInterest, numOfMonths)) /
            (Math.pow(1 + rateOfInterest, numOfMonths) - 1);

        return EMI;
    };

    // Calculate and update EMI and principal amount on cost or tenure change
    useEffect(() => {
        if (cost > 0 && tenure > 0 && Intrest > 0) {
            const loanAmt = cost - downPayment;
            const calculatedEMI = calculateEMI(loanAmt, Intrest, tenure);
            setEmi(calculatedEMI.toFixed(0));
        } else {
            setEmi(0);
        }
    }, [cost, tenure, Intrest, downPayment]);

    // Function to update down payment and recalculate EMI
    const updateDownPayment = (e, value) => {
        setDownPayment(value);
        const loanAmt = cost - value;
        const calculatedEMI = calculateEMI(loanAmt, Intrest, tenure);
        setEmi(calculatedEMI.toFixed(0));
    };

    // Calculate principal amount
    const principalAmount = cost - downPayment;

    return (
        <div className="Emi_calculater_reacted">
            <div className='Emi_firsted_calculated'>
                <div className="Emi_releted_amount">
                    <div className="Emi_first_field">
                        <div className="monthly_emi_pay">
                            <p>Monthly EMI Pay</p>
                            <p className='payment'>₹ {numberWithCommas(emi)}</p>
                        </div>
                    </div>
                    <div className="Emi_second_field">
                        <div className="Emi_loan_amount_fild">
                            <p>Loan Amount</p>
                            <input
                                type="text"
                                value={values}
                                onChange={handleChange}
                                style={{ width: "90px", outline: "none" }}
                            />
                        </div>
                        <div className='emi_slider'>
                            <Box sx={{ width: 500 }}>
                                <Slider
                                    value={downPayment}
                                    min={0}
                                    max={cost}
                                    onChange={updateDownPayment}
                                    aria-label="Default"
                                    valueLabelDisplay="auto"
                                />
                            </Box>
                        </div>
                        <div className='emi_par_anuman'>
                            <p>₹ 0</p>
                            <p>₹ {numberWithCommas(cost)}</p>
                        </div>
                        <div className='emi_par_anuman'>
                            <p>Principal Amount:</p>
                            <p className='payment'>₹ {numberWithCommas(principalAmount)}</p>
                        </div>
                    </div>
                    <div className="Emi_second_field">
                        <div className="Emi_loan_amount_fild">
                            <p>Tenure</p>
                            <input
                                type="text"
                                value={tenure}
                                onChange={(e) => setTenure(e.target.value)}
                                style={{ width: "90px", outline: "none" }}
                            />
                        </div>
                        <div className='emi_slider'>
                            <Box sx={{ width: 500 }}>
                                <Slider
                                    value={tenure}
                                    min={0}
                                    max={30}
                                    onChange={(e, value) => setTenure(value)}
                                    aria-label="Default"
                                    valueLabelDisplay="auto"
                                />
                            </Box>
                        </div>
                        <div className='emi_par_anuman'>
                            <p>5 years</p>
                            <p>30 years</p>
                        </div>
                    </div>
                    <div className="Emi_second_field">
                        <div className="Emi_loan_amount_fild">
                            <p>Interest Rate (% P.A.)</p>
                            <input
                                type="text"
                                value={Intrest}
                                onChange={(e) => setIntrest(e.target.value)}
                                style={{ width: "90px", outline: "none" }}
                            />
                        </div>
                    </div>
                </div>
            </div>
            <div className="second_emi_calculater">
                <div className='second_first_calculate'>
                    <div className="donut">
                        <Chart options={options} series={series} type="donut" width="380" />
                    </div>
                    <div className='graph_side'>
                        Are you all set to apply? Take the leap and begin your home loan journey.
                    </div>
                </div>
                <div className="Emi_second_field_calculated">
                    <div className="monthly_emi_pay">
                        <p>Principal</p>
                        <p>Amount</p>
                        <p className='payment'>₹ {numberWithCommas(principalAmount)}</p>
                    </div>
                    <div>
                        <hr style={{ color: "black", height: "90px" }} />
                    </div>
                    <div className="monthly_emi_pay">
                        <p>Interest</p>
                        <p>Amount</p>
                        <p className='payment'>₹ {numberWithCommas(emi * tenure)}</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default HomeLoan;
