import React, {useState} from "react";
import axios from 'axios';
import './index.css'
import  Navigate, { useNavigate }  from "react-router-dom";

function Create()
{
    const[values, setValues] = useState({
        name :" ",
        email : " ",
        password : " ",
        id:0
    })
    const Navigate = useNavigate();
    const handleChange = (event) =>{
        setValues ({...values, [event.target.name]:[event.target.value]})
    }


    const validateForm = (event)=>{
        event.preventDefault();
        axios.post('http://localhost:5000/singup', values)
        .then(res=>{
            Navigate("/");
        })
        .catch(err=> console.log(err));
    }

    
    return (
    <div className="signup-container">
        <div className="currentbrd">
            <form onSubmit={validateForm}>
                <h2>Registation form</h2>
                <div className="input-group" >
                    <label htmlFor="name">Name</label>
                    <input type="text"  name="name" onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>

                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input type="email"  name="email"  onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>
                
                <div className="input-group" >
                    <label htmlFor="password">Password</label>
                    <input type="password"  name="password"  onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>

                
                <button className="submit">singup</button>
            </form>
    
        </div>
    </div>
    )
}    
export default Create;
