    import React, { useState } from "react";
    import "./index.css";
    import ProductList from "./pages/productList";
    import Header from "./pages/Header";


    function App()
    {

        const [product, setproduct] = useState([
            {
                img : "/img/shoes.webp",
                name : "TRQ white Shoes",
                catogary : "shoes",
                seller : "AMZ Seller Ghz",
                price : 1500
            },
            {
                img : "/img/watch.webp",
                name : "LOREM Watch Black",
                catogary : "Watches",
                seller : "Watch Ltd Siyana",
                price : 2500
            },
            {
                img : "/img/laptop.webp",
                name : "Amz Laptop 8 gb ram",
                catogary : "Laptop",
                seller : "Delhi Laptop",
                price : 2000
            },
            {
                img : "/img/cemra.webp",
                name : "Cemra",
                catogary : "CCTV",
                seller : "AMZ CCTV Ghz",
                price : 5000
            },
            {
                img : "/img/violet.webp",
                name : "Gren Voilet",
                catogary : "Viloet",
                seller : "AMZ Gren Voilet",
                price : 3500
            },
            {
                img : "/img/botal.webp",
                name : "Bottal",
                catogary : "Botal",
                seller : "AMZ Botal",
                price : 1500
            }
        ])

        const [cart, setCart] = useState([]);
        console.log(cart);

        const addToCart = (data) =>{
            setCart(...cart, {data})
        }
        
        return( 
        <div>
            <Header></Header>
            <ProductList product= {product} addToCart={addToCart}></ProductList>
        </div>
        )
    }
    export default App;
