import React, { useState } from "react";

function EmployeeCreate() {
    const [verificationStep, setVerificationStep] = useState(0);

    const handleNextClick = () => {
        setVerificationStep(verificationStep + 1);
    }

    return (
        <>
            <div className="form_creation">
                <form>
                    {verificationStep === 0 && (
                        <>
                            <p className="para_basic">Basic Details_____________________________________________________________________________________________________</p>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Customer Name<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Email<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Gender<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Mobile<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Current address<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Permanent Address<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Pincode<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Date_of_birth<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Blood_group</label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>

                            <p className="kyc_details">Kyc-Details_____________________________________________________________________________________________________</p>

                            <div className="second_approved">
                                <div className="customer_details">
                                    <label>Pan_Number<span className="star">*</span></label>
                                    <input type="text" placeholder="Pan-number" />
                                </div>
                                <div className="customer_details">
                                    <label>pan_photo<span className="star">*</span></label>
                                    <input type="file" />
                                </div>
                            </div>

                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Aadhar Number<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Aadhar pic<span className="star">*</span></label>
                                    <input type="file" placeholder="customer-name" />
                                </div>
                            </div>

                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Account Number<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Account pic<span className="star">*</span></label>
                                    <input type="file" placeholder="customer-name" />
                                </div>
                            </div>
                            {verificationStep !== 1 && (
                                <div className="btn">
                                    <button className="next_btn" onClick={handleNextClick}>Next</button>
                                </div>
                            )}
                        </>
                    )}
                    {verificationStep === 1 && (
                        <>
                            <p className="para_basic">Loan Details_____________________________________________________________________________________________________</p>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Loan Amount<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Partner Name<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Partner code<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>Partner_Bank_Account<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>Partner_ifsc_code<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>partner payout <span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>insurance_partner_name<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                                <div className="customer_details">
                                    <label>insurance_partner_code<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>

                            <p className="kyc_details">Insurance-Details_____________________________________________________________________________________________</p>

                            <div className="second_approved">
                                <div className="customer_details">
                                    <label>insurance_partner_bank_account<span className="star">*</span></label>
                                    <input type="text" placeholder="Pan-number" />
                                </div>
                                <div className="customer_details">
                                    <label>insurance_partner_ifsc_code<span className="star">*</span></label>
                                    <input type="text" placeholder="ifc code" />
                                </div>
                            </div>

                            <div className="first_approve">
                                <div className="customer_details">
                                    <label>insurance_partner_payout<span className="star">*</span></label>
                                    <input type="text" placeholder="customer-name" />
                                </div>
                            </div>
                        </>
                    )}
                    {verificationStep !== 0 && (
                        <div className="submit_btn">
                            <input type="submit" value="submit" className="submit_bbttnn" />
                        </div>
                    )}
                </form>
            </div>
        </>
    )
}

export default EmployeeCreate;
