import React, { useState } from "react";

function Form(){

  const [values, setValues] = useState({
    name : " ",
    email : " ",
    phone : " ",
    password : " "
  })

  const [Errors, setErrors] = useState({
    name : " ",
    email : " ",
    mobile : " ",
    password : " "
  })


  const handleChange = (e) =>{
    const [name, value] = e.target;
    setValues({...values, [name]:values})

    let errorMessage = ' ';

    if(name === 'name' && value.length < 5){
      errorMessage('* name is required')
    }
    if(name === 'email' && !/\S+@\S+\.\S+/.test(value)){
      errorMessage(' * email is not vailed ')
    }
    if(mobile === 'mobile ' && value.length !=10){
      errorMessage(' * mobile number not vailed ')
    }
    if(password === 'password' && value.length !=8){
      errorMessage(' * password mostly digit ')
    }
  }


  const handleSubmit = (e) =>{
    e.preventDefault();

  }

  return(
    <>
        <form onSubmit={handleSubmit}>
          <div>Form</div>
          <div>
            <label>Enter name</label>
            <input type="text" name="name" placeholder="name" onChange={handleChange} />
          </div>

          <div>
            <label>Enter email</label>
            <input type="email" name="email" placeholder="email" onChange={handleChange} />
          </div>

          <div>
            <label>Enter phone</label>
            <input type="number" name="mobile" placeholder="phone" onChange={handleChange} />
          </div>

          <div>
            <label>Enter password</label>
            <input type="password" name="password" placeholder="password" onChange={handleChange} />
          </div>
          <input type="submit" value="create"></input>
        </form>
    </>
  )
}
export default Form
