import React, { useState } from "react";

function Form() {
  const [values, setValues] = useState({
    name: '',
    email: '',
    mobile: '',
    password: ''
  });

  const [error, setError] = useState({
    name: '',
    email: '',
    mobile: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
    let errorMessage = '';

    if (name === 'email' && !/\S+@\S+\.\S+/.test(value)) {
      errorMessage = '* Email not valid';
    }
    if (name === 'name' && value.length < 5) {
      errorMessage = '* Name is required';
    }
    if (name === 'mobile' && !/^[0-9]{10}$/.test(value)) {
      errorMessage = '* Mobile number not valid';
    }
    if (name === 'password' && value.length < 6) {
      errorMessage = '* Password must be at least six digits';
    }

    setError({ ...error, [name]: errorMessage });
  };

  function validateForm() {
    let newError = {};
    if (!values.name) newError.name = '* Name is required';
    if (!values.email) newError.email = '* Email is required';
    if (!values.mobile) newError.mobile = '* Mobile number is required';
    if (!values.password) newError.password = '* Password is required';

    setError(newError);
    return Object.keys(newError).length === 0;
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (validateForm()) {
      console.log(values);
    }
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Enter Name</label>
          <input
            type="text"
            autoComplete="off"
            placeholder="Enter name"
            name="name"
            value={values.name}
            onChange={handleChange}
          /> <br />
          {error.name && <span className="error">{error.name}</span>}
        </div>

        <div>
          <label>Enter Email</label>
          <input
            type="text"
            autoComplete="off"
            placeholder="Enter email"
            name="email"
            value={values.email}
            onChange={handleChange}
          /> <br />
          {error.email && <span className="error">{error.email}</span>}
        </div>

        <div>
          <label>Enter Mobile</label>
          <input
            type="number"
            autoComplete="off"
            placeholder="Enter number"
            name="mobile"
            value={values.mobile}
            onChange={handleChange}
          /> <br />
          {error.mobile && <span className="error">{error.mobile}</span>}
        </div>

        <div>
          <label>Enter Password</label>
          <input
            type="password"
            autoComplete="off"
            placeholder="Enter password"
            name="password"
            value={values.password}
            onChange={handleChange}
          /> <br />
          {error.password && <span className="error">{error.password}</span>}
        </div>

        <input type="submit" value="Create" />
      </form>
    </>
  );
}

export default Form;
