import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';

//create action

export const createUser = createAsyncThunk("createUser", async (data, {rejectWithValue})=>{

    const response = await axios.post("http://localhost:5000/singup",data);

    try{
        const result = await response.json(data);
        return result;
    }catch(error){
        return rejectWithValue(error);
    }


})

// read action
export const showUser = createAsyncThunk("showUser", async (_, { rejectWithValue }) => {
    try {
        const response = await axios.get("http://localhost:5000/");
        return response.data; // Accessing data property directly
    } catch (error) {
        return rejectWithValue(error);
    }
});

//delete action/delete/:id

export const deleteUser = createAsyncThunk("deleteUser", async(id, {rejectWithValue})=>{
    try {
        const response = axios.delete("http://localhost:5000/delete/:id");
        return response;
    } catch (error) {
        return rejectWithValue(error);
    }
})

export const userDetail = createSlice({
    name: "userDetail",
    initialState: {
        user: [],
        loading: false,
        error: null,
    },
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(createUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(createUser.fulfilled, (state, action) => {
                state.loading = false;
                state.user.push(action.payload);
            })
            .addCase(createUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })
             .addCase(showUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(showUser.fulfilled, (state, action) => {
                state.loading = false;
                state.user = action.payload;
            })
            .addCase(showUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            });
            
    },
});


export default userDetail.reducer;
