import React, {useState} from "react";
import axios from 'axios';
import  Navigate, { useNavigate }  from "react-router-dom";
import { useDispatch } from "react-redux";
import { createUser } from "../features/userDatailsSlice";

function Create()
{
    const[users, setUsers] = useState({});

    const dispatch = useDispatch();

    const handleChange = (event) =>{
        setUsers ({...users, [event.target.name]:[event.target.value]})
    }


    const validateForm = (event)=>{
        event.preventDefault();
       dispatch(createUser(users));
    }

    
    return (
    <div className="signup-container">
        <div className="currentbrd">
            <form onSubmit={validateForm}>
                <h2>Registation form</h2>
                <div className="input-group" >
                    <label htmlFor="name">Name</label>
                    <input type="text"  name="name" onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>

                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input type="email"  name="email"  onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>
                
                <div className="input-group" >
                    <label htmlFor="password">Password</label>
                    <input type="password"  name="password"  onChange={handleChange} required/><b><span className="formerror"></span></b>
                </div>

                
                <button className="submit">singup</button>
            </form>
    
        </div>
    </div>
    )
}    
export default Create;
