import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';

//create action

export const createUser = createAsyncThunk("createUser", async (data, {rejectWithValue})=>{
    try{
        const response = await axios.post("http://localhost:5000/singup",data);
        return response;
    }catch(error){
        return rejectWithValue(error);
    }


})

// read action
export const showUser = createAsyncThunk("showUser", async ( { rejectWithValue }) => {
    try {
        const response = await axios.get("http://localhost:5000/");
        return response.data; // Accessing data property directly
    } catch (error) {
        return rejectWithValue(error);
    }
});


//delete action/delete/:id


export const deleteUser = createAsyncThunk("deleteUser", async(id, {rejectWithValue})=>{

    if (window.confirm("Are you sure you want to delete this item?")) {
        try {
            const response = axios.delete("http://localhost:5000/delete/"+id);
            return response;
            
        } catch (error) {
            return rejectWithValue(error);
        }
    }

    
})

// update action

export const updateUser = createAsyncThunk(
    "updateUser",
    async (data, { rejectWithValue }) => {
      const { id } = data; 
      if (window.confirm("क्या आप वाकई इस आइटम को अपडेट करना चाहते हैं?")) {
        try {
          const response = await axios.put(`http://localhost:5000/update/${id}`, data);
          return response.data;
        } catch (error) {
          return rejectWithValue(error.response.data);
        }
      }
    }
  );

export const userDetail = createSlice({
    name: "userDetail",
    initialState: {
        user: [],
        loading: false,
        error: null,
    },
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(createUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(createUser.fulfilled, (state, action) => {
                state.loading = false;
                state.user.push(action.payload);
            })
            .addCase(createUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })
             .addCase(showUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(showUser.fulfilled, (state, action) => {
                state.loading = false;
                state.user = action.payload;
            })
            .addCase(showUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })
            .addCase(deleteUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(deleteUser.fulfilled, (state, action) => {
                state.loading = false;
                const  id  = action.payload;
                if(id){
                    state.user = state.user.filter((d)=>d.id !== id);
                }
            })
            .addCase(deleteUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })
            .addCase(updateUser.pending, (state) => {
                state.loading = true;
            })
            .addCase(updateUser.fulfilled, (state, action)=>{
                state.loading = false;
                state.user = state.user.map((d)=>{
                    return d.id === action.payload.id ? action.payload : d; // Update karte samay return statement add kiya gaya hai
                })
            })
            .addCase(updateUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })
    },
});


export default userDetail.reducer;
